package JavaProgram;

public class fizBuzzProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		
		if(i%5 ==0 && i%3 ==0)
		{
			System.out.println("FizzBuzz");
		}
		else if(i%5 ==0)
		{
			System.out.println("Buzz");
		}
		else if(i%3 ==0)
		{
			System.out.println("FizzBuzz");
		}
			

	}

}
